#include <bits/stdc++.h>
using namespace std;
 class SinhVien{ private: string msv,ht,lop,mail; public: friend istream &operator >> (istream &in,SinhVien &a); friend ostream &operator << (ostream &out,SinhVien a); string getmsv(){ return msv; } string getlop(){ return lop; } }; istream &operator >> (istream &in,SinhVien &a){ in>>a.msv; in.ignore(); getline(in,a.ht); in>>a.lop>>a.mail; return in; } string nganh(string s){ stringstream ss(s); string res=""; string tmp; while(ss>>tmp){ res+=toupper(tmp[0]); } return res.substr(0,2); } ostream &operator << (ostream &out,SinhVien a){ out<<a.msv<<" "<<a.ht<<" "<<a.lop<<" "<<a.mail<<endl; return out; } int main(){ int n; cin>>n; SinhVien a[n]; for(int i=0;i<n;i++) cin>>a[i]; int q; cin>>q;cin.ignore(); while(q--){ string s; getline(cin,s); for(int i=0;i<s.length();i++){ s[i]=toupper(s[i]); } cout<<"DANH SACH SINH VIEN NGANH "<<s<<":"<<endl; for(int i=0;i<n;i++){ if(nganh(s)=="AT" || nganh(s)=="CN"){ if(a[i].getlop()[0]!='E' && nganh(s)==a[i].getmsv().substr(5,2)) cout<<a[i]; } else{ if(nganh(s)==a[i].getmsv().substr(5,2)) cout<<a[i]; } } } }
